package programs;

public class Asterics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=5;i=i+1){
			System.out.println("");
			//System.out.print(i);
			for(j=1;j<=i;j=j+1){
				System.out.print("*");
			}
		}
		for(i=5;i>=0;i=i-1){
			System.out.println("");
			//System.out.print(i);
			for(j=0;j<=i;j=j+1){
				System.out.print
				("*");
			}
		}

	}

}
