package programs;

public class Printasterics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		//String s1=Integer.toString(i);
		//System.out.println(s1);
		for(i=5;i>0;i=i-1){
			System.out.println("");
			//System.out.println(i);
			for(int j=0;j<=i;j=j+1){
			
				System.out.print("*");
			
				System.out.print(" ");
			}
		}
		//System.out.println("*");
}
}



	


